import React, { Component } from 'react'
import  { connect } from 'react-redux'
import {Link} from 'react-router'


import {
    fetchWatches,
    fetchCategories, 
    loadMoreWatches,
    addWatchToCart
} from 'actions'

import { getWatches } from 'selectors'
import R from 'ramda'

class Watches extends Component  {
    componentDidMount () {
        this.props.fetchWatches(),
        this.props.fetchCategories()
    }

    renderWatch(watch,index) {
        const {addWatchToCart} = this.props
        const shortDescription = `${R.take(60, watch.description)}...`

        return(
            <div className='col-sm-4 col-lg-4 col-md-4 book-list' key={index}>
                <div className="thumbnail">
                <img
                    className='img-thumbnail'
                    src={watch.image}
                    alt={watch.name}
                />
                <div className='caption'>
                    <h4 className='pull-right'>${watch.price}</h4>
                    <h4>
                        <Link to={`/watches/${watch.id}`}>
                            {watch.name}
                        </Link>
                    </h4>
                    <p>{shortDescription}</p>
                    <p className='itemButton'>
                        <button
                            className='btn btn-primary'
                            onClick={() => addWatchToCart(watch.id)}
                        >
                            Buy Now!
                        </button>
                        <Link
                            to={`/watches/${watch.id}`}
                            className='btn btn-default'
                        >
                            More info
                        </Link>
                    </p>
                </div>
                </div>
            </div>
        )
    }

    render() {
        const {watches, loadMoreWatches} = this.props
        return (
            <div>
                <div className='books row'>
                    {watches.map((watch, index) => this.renderWatch(watch,index))}
                </div>
                    <div className='row'>
                        <div className='col-md-12'>
                            <button
                                onClick={loadMoreWatches}
                                className='pull-right btn btn-primary'
                            >
                                Load more
                            </button>
                        </div>
                    </div>
            </div> 
        )
    }
}
     
const mapStateToProps =(state,ownProps) => {
    return {
        watches: getWatches(state,ownProps)
      }
}

const mapDispatchToProps = {
    fetchWatches,
    fetchCategories,
    loadMoreWatches,
    addWatchToCart
}    



export default connect(mapStateToProps, mapDispatchToProps)(Watches)